import './App.css';
import React from 'react'
import Info from './components/info';
import EdInfo from './components/edInfo';
import uniqid from 'uniqid'

function App() {

  const [name, setName] = React.useState({firstName: '', lastName : ''})
  const [ed, setEd] = React.useState({id: uniqid(), name:'', year: ''})
  const [edL, setEdL] = React.useState([])

  function $(x){
    return document.getElementById(x)
  }

  function handleChange(e){
      const {name, value} = e.target

      setName(prevState => ({...prevState, [name]:value}))
      setEd(prevState => ({...prevState, [name]:value}))
  }

  function handleAdd(e){
    const uniName = $('name').value
    const year = $('year').value
    setEdL(prevState => [...prevState, {name:uniName, year:year}])
    console.log('EDL: ' + edL)
  }

  return(
      <div className='form'>
          <div className='container'>
              <input type='text' name='firstName' placeHolder='First name'onChange={handleChange} value={name.firstName}/>
              <input type='text' name='lastName' placeHolder='Last name'onChange={handleChange} value={name.lastName}/>
          </div>   
          <br/>
          <div className='container'>
              <input type='text' name='name' placeHolder='Uni Name' onChange={handleChange} value={ed.name} id='name'/>
              <input type='text' name='year' placeHolder='Year' onChange={handleChange} value={ed.year} id='year'/>
              <button onClick={handleAdd}>Add</button>
          </div>   

          <div>
            <h2>Preview</h2>
            <Info user={name} />
            <EdInfo user={edL} />
          </div>
      </div>   
  )
}
export default App;
