import './App.css';
import React from 'react'
import Info from './components/info';
import uniqid from 'uniqid'
import Header from './components/header'
import 'bootstrap/dist/css/bootstrap.min.css';


function App() {

  const [info, setInfo] = React.useState({firstName: '', lastName : '', email : '', areaCode: '', phoneNumber : ''})
  const [edL, setEdL] = React.useState([])

  function $(x){
    return document.getElementById(x)
  }

  function handleChange(e){
      const {name, value, id} = e.target

      setInfo(prevState => ({...prevState, [name]:value}))

      setEdL(prevState => {
        const arr = []
        for(let i=0; i<prevState.length; i++) {
          const currentInput = prevState[i]
          if(currentInput.id === e.target.parentElement.id) {
            const updatedInput = {
              ...currentInput,
              [name]:value
            }
            arr.push(updatedInput)
          } else {
            arr.push(currentInput)
          }
        }
        return arr
      })
  }

  function handleAdd(e){
    setEdL(prevState => [...prevState, {id: uniqid(), name:'-', year:'-'}])
  }

  function handleDelete(e){
    setEdL(prevState => prevState.filter(each => each.id != e.target.id))
  }

  return(
    <div className='main-container'>

      <div className='form'>
        <Header />
          <div className='container'>
              <input type='text' name='firstName' placeHolder='First name'onChange={handleChange} value={info.firstName}/>
              <input type='text' name='lastName' placeHolder='Last name'onChange={handleChange} value={info.lastName}/>
              <input type='email' name='email' placeHolder='Email'onChange={handleChange} value={info.email}/>
              <Container fluid>
                <Row>
                  <Col><input type='tel' name='areaCode' placeHolder='Area Code'onChange={handleChange} value={info.areaCode} maxlength='3'/></Col>
                  <Col><input type='tel' name='phoneNumber' placeHolder='Phone Number'onChange={handleChange} value={info.phoneNumber} maxlength='7'/></Col>
                  </Row>
              </Container>
          </div>   
          <br/>
          <div className='container'>
              <button onClick={handleAdd}>Add</button>
              <h3>Education</h3>
              {edL.map(each=>
              <div key={each.id} className="education-card"
              id = {each.id}>
                <input type='text' name='universityName' placeHolder='University Name' onChange={handleChange} />
                <input type='date' name='year' placeHolder='Year' onChange={handleChange} />  
                <button id={each.id} onClick={handleDelete}>Delete</button>
              </div>
              )}

          </div>  
      </div>   

          <div className='cv-preview'>

            <Info user={info} />
            {edL.map(each=>
              <div key={each.id}>
                <div>{each.universityName}</div>  
                <div>{each.year}</div>  
              </div>
              )}
          </div>

    </div>
  )
}
export default App;
