import React from 'react'
import ReactDOM from 'react-dom'
import Education from './components/Education'



function App(){
    const [educationList, setEducationList] = React.useState([<Education/>])

    function addEducation(){

        setEducationList(prevState => [...educationList, <Education/>])
    }

    function handleDelete(e, id){
        setEducationList(prevState => {
            return prevState.filter((each)=> each.id !== e.target.id)
        })

        console.log('the id of delete btn click: ', e.target.id)
    }

    const currentEducationList = educationList.map(each =>
        <div>
            {each}
            <button onClick={handleDelete}>Delete</button>

        </div>
        )

    return(
        <div>
            <h1>Education List</h1>
            <button onClick={addEducation}>Add Education</button>
            {currentEducationList}
        </div>

    )
}

ReactDOM.render(<App />, document.getElementById('root'))
