import React from 'react'
import uniqid  from 'uniqid'

function Education(){
    const [education, setEducation] = React.useState({id: '', name: '', last: ''})

    function handleChange(e){
        const {name, value, id} = e.target
        console.log('the unique id: ', education.id)
        setEducation(prevState => {
            return {
                ...prevState,
                [id]: uniqid(),
                [name]: value

            }

        })
    }


    return(
        <div className='form-container'>
            <input type='text' onChange={handleChange}placeholder='Name' name='name' value={education.name}/>
            <input type='text' onChange={handleChange}placeholder='Last' name='last' value={education.last}/>
        </div>

    )
}

export default Education
