import React from 'react'
import uniqid  from 'uniqid'

function Education(){
    const [education, setEducation] = React.useState({id: uniqid(), name: '', last: ''})

    function handleChange(e){
        const {name, year, value} = e.target

        setEducation(prevState => {
            return {
                ...prevState,
                [name]: value
            }
            console.log(prevState.id)
        })
    }


    return(
        <div className='form-container'>
            <input type='text' onChange={handleChange}placeholder='Name' name='name' value={education.name}/>
            <input type='text' onChange={handleChange}placeholder='Last' name='last' value={education.last}/>
        </div>

    )
}

export default Education
