import React from 'react'

function Education(){
    const [education, setEducation] = React.useState({name: '', year: ''})

    function handleChange(e){
        setEducation(prevState => {prevState.name = e.target.value})
    }

    function handleEditClick(e){
        console.log("education.name is: ", education.name)
    }

    return(
        <div className='form-container'>
            <input type='text' onChange={handleChange}placeholder='Name' name='name'/>
            <button onClick={handleEditClick}>Edit</button>
        </div>

    )
}

export default Education
